mapper.insert:

insert into order (id, orderSerialNum, meaId, 
  orderStatus, payStatus, actualAmount, 
  payAmount, payType, userId, 
  managerId, createTime, updateTime
  )
values (#{id,jdbcType=BIGINT}, #{orderserialnum,jdbcType=VARCHAR}, #{meaid,jdbcType=INTEGER}, 
  #{orderstatus,jdbcType=VARCHAR}, #{paystatus,jdbcType=VARCHAR}, #{actualamount,jdbcType=INTEGER}, 
  #{payamount,jdbcType=INTEGER}, #{paytype,jdbcType=VARCHAR}, #{userid,jdbcType=BIGINT}, 
  #{managerid,jdbcType=BIGINT}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}
  )

mapper.select:
  select 
  
  from order
  where 1=1 
 
		and orderStatus =  #{orderStatus}
	
	
		and payStatus =  #{payStatus}
	
	
		and payType =  #{payType}
